import React, { Component } from "react";

import Nav from "./Nav";
import Hog from "./Hog";

class HogsList extends Component {
  Base_URL = "http://localhost:3000/hogs";

  constructor() {
    super();
    this.state = {
      hogs: []
    };
  }

  componentDidMount() {
    const that = this;
    fetch("http://localhost:3000/hogs")
      .then(resp => {
        debugger;
        return resp.json();
      })
      .then(data => that.setState({ hogs: data }));
    return null;
  }
  render() {
    return (
      <div>
        <Hog showHog={this.state.hogs} />
      </div>
    );
  }
}

export default HogsList;
