import React, { Component } from "react";
import imagesFile from "../hog-imgs";

class Hog extends Component {
  importAll(r) {
    let images = {};
    r.keys().map((item, index) => {
      images[item.replace("./", "")] = r(item);
    });
    return images;
  }
  images = importAll(require.context("./images", false, "/.png/"));

  componentDidMount() {
    images=this.importAll(require.context("./images", false, "/.png/"));
  }
  render() {
    return (
      <div>
        <p>{this.props.showHog.name}</p>
        <img
          src={imagesFile + this.props.showHog.name.toLowerCase() + ".jpg"}
        ></img>
      </div>
    );
  }
}

export default Hog;
