import React, { Component } from "react";
import "./App.css";
import Nav from "./components/Nav";
import HogsList from "./components/HogsList";

class App extends Component {
  state = {
    showingGreased: false,
    sortByName: false
  };

  toggleGreased = () => {
    this.setState({ showingGreased: !this.state.showingGreased });
  };

  nameSorting=()=>{
  this.setState({sortByName: !this.state.showingGreased})
  }
  render() {
    return (
      <div className="App">
        <Nav
          filterGreased={this.state.showingGreased}
          toggleGreased={this.toggleGreased}
          sortByName={this.state.sortByName}
        />
        <HogsList filterGreased={this.state.showingGreased} />
      </div>
    );
  }
}

export default App;
