import React, { Component } from "react";

import Nav from "./Nav";
import Hog from "./Hog";

class HogsList extends Component {
  Base_URL = "http://localhost:3000/hogs";

  constructor() {
    super();
    this.state = {
      hogs: []
    };
  }

  static getDerivedStateFromProps = () => {
    fetch(this.Base_URL)
      .then(resp => resp.json())

  };

  mappingHogs = () => {
    this.state.hogs.map(function(hog) {
      hog;
    });
  };
  render() {
    return (
      <div>
        <Hog showHog={this.mappingHogs} />
      </div>
    );
  }
}

export default HogsList;
