import React, { Component } from "react";

class Hog extends Component {
  getImgUrl() {
    let image = require(`../hog-imgs/${this.props.showHog.name}.jpg`);
    return image;
  }
  render() {
    return (
      <div>
        <p>{this.props.showHog.name}</p>
        <img
          src={this.hogImages[this.props.showHog.name.toLowerCase() + ".jpg"]}
        ></img>
      </div>
    );
  }
}

export default Hog;
