import React, { Component } from "react";

class Hog extends Component {
  getImg() {
    let image = require(`../hog-imgs/${this.props.showHog.name
      .split(" ")
      .join("_")
      .toLowerCase()}.jpg`);
    return image;
  }

  showHogInfo = () => {};
  render() {
    return (
      <div>
        <h5>{this.props.showHog.name}</h5>
        <p></p>
        <img src={this.getImg()} onClick={this.showHogInfo}></img>
      </div>
    );
  }
}

export default Hog;
