import React, { Component } from "react";

class Hog extends Component {
  hogImages = {};
  importAll(r) {
    let images = {};
    r.keys().map((item, index) => {
      images[item.replace("./", "")] = r(item);
    });
    return images;
  }

  componentDidMount() {
    this.hogImages = this.importAll(
      require.context("../hog-imgs", false, "/.jpg/")
    );
  }
  render() {
    return (
      <div>
        <p>{this.props.showHog.name}</p>
        <img
          src={this.hogImages[this.props.showHog.name.toLowerCase()] + ".jpg"}
        ></img>
      </div>
    );
  }
}

export default Hog;
