import React, { Component } from "react";

class Hog extends Component {
  getImg() {
    let image = require(`../hog-imgs/${this.props.showHog.name
      .toLowerCase()
      .sp(" ", "_")}.jpg`);
    return image;
  }
  render() {
    return (
      <div>
        <p>{this.props.showHog.name}</p>
        <img src={this.getImg()}></img>
      </div>
    );
  }
}

export default Hog;
