import React, { Component } from "react";

import Nav from "./Nav";
import Hog from "./Hog";

class HogsList extends Component {
  Base_URL = "http://localhost:3000/hogs";

  constructor() {
    super();
    this.state = {
      hogs: []
    };
  }

  static getDerivedStateFromProps = () => {
    fetch(this.Base_URL)
      .then(resp => resp.json())
      .then(data => {
        this.setState({
          hogs: data
        });
      });
  };

  render() {
    return (
      <div>
        <Hog showHog={this.state.hogs} />
      </div>
    );
  }
}

export default HogsList;
